app = Flask(__name__)

# Combine the essays and labels into a single list
essays = ai_essays + human_essays
labels = [1] * len(ai_essays) + [0] * len(human_essays)

# Convert the essays and labels to a 2D array
essays = np.array(essays).reshape(-1, 1)
labels = np.array(labels)

# Convert the essays to a numerical representation
vectorizer = CountVectorizer()
essays = vectorizer.fit_transform(essays[:, 0])

# Train the classifier
model = MultinomialNB()
model.fit(essays, labels)

@app.route('/')
def index():
    return render_template('index.html')

@app.route('/predict', methods=['POST'])
def predict():
    essay = request.form['essay']
    essay = essay.replace("\n", "")
    essay = essay.replace("\"", "")
    essay = vectorizer.transform([essay])
    prediction = model.predict(essay)[0]

    if prediction == 1:
        prediction_text = "The text was written by an AI."
    else:
        prediction_text = "The text was written by a human."

    return render_template('index.html', prediction_text=prediction_text)

if __name__ == '__main__':
    app.run(host="0.0.0.0")
